document.addEventListener("DOMContentLoaded", () => {
    let counter = document.getElementById("counter");
    let plusBtn = document.getElementById("plus");
    let minusBtn = document.getElementById("minus");
    let likeBtn = document.getElementById("heart");
    let pauseBtn = document.getElementById("pause");
    let submitBtn = document.getElementById("submit");
    let commentList = document.getElementById("list");
    let likesList = document.querySelector(".likes");
    let commentInput = document.getElementById("comment-input");

    let count = 0;
    let isPaused = false;
    let likes = {};

    function updateCounter() {
        counter.textContent = count;
    }

    let timer = setInterval(() => {
        if (!isPaused) {
            count++;
            updateCounter();
        }
    }, 1000);

    plusBtn.addEventListener("click", () => {
        count++;
        updateCounter();
    });

    minusBtn.addEventListener("click", () => {
        count--;
        updateCounter();
    });

    likeBtn.addEventListener("click", () => {
        if (!likes[count]) {
            likes[count] = 1;
            let likeItem = document.createElement("li");
            likeItem.id = `like-${count}`;
            likeItem.textContent = `${count} has been liked 1 time`;
            likesList.appendChild(likeItem);
        } else {
            likes[count]++;
            let likeItem = document.getElementById(`like-${count}`);
            likeItem.textContent = `${count} has been liked ${likes[count]} times`;
        }
    });

    pauseBtn.addEventListener("click", () => {
        if (!isPaused) {
            clearInterval(timer);
            isPaused = true;
            pauseBtn.textContent = "resume";
            plusBtn.disabled = true;
            minusBtn.disabled = true;
            likeBtn.disabled = true;
            submitBtn.disabled = true;
        } else {
            timer = setInterval(() => {
                if (!isPaused) {
                    count++;
                    updateCounter();
                }
            }, 1000);
            isPaused = false;
            pauseBtn.textContent = "pause";
            plusBtn.disabled = false;
            minusBtn.disabled = false;
            likeBtn.disabled = false;
            submitBtn.disabled = false;
        }
    });

    document.getElementById("comment-form").addEventListener("submit", (e) => {
        e.preventDefault();
        let commentText = commentInput.value.trim();
        if (commentText !== "") {
            let comment = document.createElement("p");
            comment.textContent = commentText;
            commentList.appendChild(comment);
            commentInput.value = "";
        }
    });
});
